#Combien y a-t-il de handle dans cette array ?
def handle_count (list)
  list_length = list.length
  #puts list.count

  puts "il y a #{list_length} éléments dans l'array"
end

#Quel est le handle le plus court de cette liste ?
def handle_min (list)
  min = list.min_by { |value| value.size}
  puts "le handle le plus court est #{min} !"

  #puts list.min { |value, item| value.size <=> item.size}
  #puts list.min_by { |value| value.size}
end


#Combien y-a-t'il de handle contenant 5 caractères (le @ ne compte pas pour un caractère)
def count_length (list)
  list_5_length = list.count {|item| item.length == 6}
  puts "il y a #{list_5_length} éléments à 5 caractères"
end

#Combien commencent par une majuscule (première lettre juste après le @) ?
def handle_UPPER (list)

  #Compter le nombre d'adresse commençant par un "_"
  underscore_number = list.count{|item| item =~/^@_/}
  puts "il y a #{underscore_number} qui possède des underscores"
  
  #Compter le nombre d'adresse commençant par une majuscule
  upper_number = list.count {|item| item =~ /\A@[[:upper:]]/}
  puts "il y a #{upper_number-underscore_number} handles commençant avec une majuscule"
end
#Trie la liste de handle par ordre alphabétique.
def alphabetic(list)
  alphabetic = list.sort
  puts alphabetic
end
#Trie la liste de handle par taille des handle (les plus petits en premiers, les plus grands après)

def handle_by_size(list)
  by_size = list.sort_by(&:length)
  puts by_size
end
#Quelle est la position dans l'array de la personne @epenser ?
def handle_position(list)
  index = list.index {|item| item == "@epenser"}
  
  puts "La position de @epenser est #{index}"
end
#Sors-moi une répartition des handle par taille de ces derniers (nombre de handle avec 1 caractère, nombre de handle avec 2 caractères, nombre de handle avec 3 caractères, etc)
def size_handle(list)
  
  for i in 1..list.length do
    handles_number = list.count {|item| item.length == i}
    i+=1
    #si handles_number == 0, ne rien afficher
    if handles_number == 0
      print ""
    else
      puts "il y a #{handles_number} handles à #{i} caractères"
    end
    
  end
  
end

def perform
  list = handle
  handle_count(list)
  count_length(list)
  handle_min(list)
  handle_UPPER(list)
  alphabetic(list)
  handle_by_size(list)
  handle_position(list)
  size_handle(list)
  
end

